  `id_loai_cau_hoi` int(11) NOT NULL,
  `cau_hoi` varchar(1000) NOT NULL,
  `nd_cau_hoi` varchar(5000) DEFAULT NULL,
  `audio_cau_hoi` varchar(250) DEFAULT NULL,
  `dap_an_1` varchar(1000) NOT NULL,
  `dap_an_2` varchar(1000) DEFAULT NULL,
  `dap_an_3` varchar(1000) DEFAULT NULL,
  `dap_an_4` varchar(1000) DEFAULT NULL,
  `dap_an_dung` varchar(1000) NOT NULL,
  `dap_an_dung1` varchar(1000) DEFAULT NULL,
  `loi_giai` varchar(1000) NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `bai_tap`
